import React, { useState } from "react";
import InvoiceList from "./InvoiceList";

const Invoice = () => {
        const [value, setValue] = useState([]);

        async function dataHandler() {
            const response = await fetch("https://rscdev.taxadda.com/api/invoice/list")
            const data = await response.json();
                const transformedVals = data.map(individualData => {
                    return {
                        id: individualData._id,
                        name: individualData.name,
                        dueDate: individualData.dueDate,
                        billNo: individualData.billNo,
                        billDate: individualData.billDate,
                        productId: individualData.lineItem[0]._id,
                        productName: individualData.lineItem[0].productName,
                        quantity: individualData.lineItem[0].quantity,
                        price: individualData.price,
                        amount: individualData.amount,
                        gstRate: individualData.gstRate,
                        notes: individualData.notes,
                        status: individualData.status
                    }
                })
                setValue(transformedVals);
        }


        return (
            <>
                <div className="container bg-light mt-5 rounded p-2 shadow" style={{maxwidth: "700px"}}>
                    <h4 className="display-4 px-3 fw-bold">Invoices</h4>
                    <button onClick={dataHandler}>Show Invoices</button> 
                    <div className="row">
                        <nav className="col">
                               {value}
                        </nav>    
                    </div>                    
                </div>
            </>
        )
}

export default Invoice;
