import React, { useState } from "react";
import InvoiceList from "./InvoiceList";

const Invoice = () => {
        const [value, setValue] = useState([]);

        function fetchMoviesHandler() {
            fetch('https://rscdev.taxadda.com/api/invoice/list')
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                const name = data.invoice
                const transformData = data.invoices.map(dataVal => {
                    return {
                        key: dataVal._id, 
                        name: dataVal.name,
                        dueDate: dataVal.dueDate,
                        billNo: dataVal.billNo,
                        billDate: dataVal.billDate,
                        grossAmount: dataVal.grossAmount,
                        gstAmount: dataVal.gstAmount,
                        netAmount: dataVal.netAmount,
                        notes: dataVal.notes,
                        status: dataVal.status,
                        createdAt: dataVal.createdAt,
                        updatedAt: dataVal.updatedAt,                        

                        productName: dataVal.productName,
                        quantity: dataVal.quantity,
                        price: dataVal.price,
                        amount: dataVal.amount,
                    };
                })
                setValue(transformData);
            })
        }        

        fetchMoviesHandler();

        return (
            <>
                <div className="container bg-light mt-5 rounded p-2 shadow" style={{maxwidth: "700px"}}>
                    <h4 className="display-4 px-3 fw-bold">Invoices</h4>
                    <InvoiceList values={value} />                        
                </div>
            </>
        )
}

export default Invoice;
