import React from "react";
import InputBox from "./Invoice/InputInvoice/InputBox";
import Invoices from "./Invoice/Invoices";
import Search from "./Invoice/SearchInvoice/Search";
import useFetch from "./useFetch";


function App() {

const {data, loading, error} = useFetch('https://rscdev.taxadda.com/api/invoice/list');

  if(loading){ 
    return (
        <div class="container">
        <div class="row">
          <div id="loader">
              <div class="dot"></div>
              <div class="dot"></div>
              <div class="dot"></div>
              <div class="dot"></div>
              <div class="dot"></div>
              <div class="dot"></div>
              <div class="dot"></div>
              <div class="dot"></div>
              <div class="lading"></div>
          </div>
          </div>
        </div>
      )
  }

  if(error) console.log(error);

  let inputData = {};

  const onSubmitNewData = (newData) => {
    inputData = newData;
  }



  return (
    <div className="App">
     <nav className="navbar nav-expand navbar-dark bg-primary">
        <ul className="navbar-nav fs-5 text-white">
            <h1 className="position-center">INVOICE APP</h1>
        </ul>
     </nav>
       <InputBox newInvoiceData={onSubmitNewData}/>
       <Invoices data={data} inputedData = {inputData}/>
    </div>

  );
}

export default App;
