import React, { useState } from "react";
import InvoiceList from "./InvoiceList";

const Invoice = () => {
        const [value, setValue] = useState([]);

        const dataHandler = () => {
            fetch("https://rscdev.taxadda.com/api/invoice/list").then((response)=> {
                return response.json();
            }).then((data) => {
                setValue(data.invoices[6])
            })
        }

        return (
            <>
                <div className="container bg-light mt-5 rounded p-2 shadow" style={{maxwidth: "700px"}}>
                    <h4 className="display-4 px-3 fw-bold">Mark my words</h4>
                    <div className="row">
                        <nav className="col">
                            <button onClick={dataHandler}>List in</button>
                            <InvoiceList val={value}/>
                        </nav>
                    </div>
                </div>
            </>
        )
}

export default Invoice;
